        return <div>Waiting...</div>
    }
     
    return (
        <section className='home'>
            <div className='home_journal-container'>
                <h1 className='home_header'>Welcome to your journal Jaz</h1>
                <div className='home_jounal-list'>
                  <JournalEntry />
                </div>
            </div>
        </section>

    )
};

export default Home;
